import React, { useEffect, useState } from "react";
import axios from "axios";

const PRODUCER_API = process.env.REACT_APP_PRODUCER_API || "http://localhost:3001";
const WS_URL = process.env.REACT_APP_WS_URL || "ws://localhost:4000";

function App() {
  const [auctionId, setAuctionId] = useState("auction-1");
  const [userId, setUserId] = useState("user-" + Math.floor(Math.random() * 1000));
  const [amount, setAmount] = useState("");
  const [highestBids, setHighestBids] = useState({});
  const [log, setLog] = useState([]);

  useEffect(() => {
    const ws = new WebSocket(WS_URL);
    ws.onopen = () => pushLog("WS connected");
    ws.onmessage = (ev) => {
      const msg = JSON.parse(ev.data);
      if (msg.type === "snapshot") {
        setHighestBids(msg.highestBids || {});
        pushLog("Received snapshot");
      } else if (msg.type === "highestBid") {
        setHighestBids((prev) => ({ ...prev, [msg.auctionId]: msg.highest }));
        pushLog(`Highest updated for ${msg.auctionId}: ${msg.highest.amount}`);
      } else if (msg.type === "bidReceived") {
        pushLog(`Bid received (not highest) on ${msg.auctionId}: ${msg.bid.amount}`);
      } else {
        pushLog("Unknown message: " + JSON.stringify(msg));
      }
    };
    ws.onclose = () => pushLog("WS disconnected");
    ws.onerror = () => pushLog("WS error");

    return () => ws.close();
  }, []);

  function pushLog(text) {
    setLog((l) => [`${new Date().toLocaleTimeString()} - ${text}`, ...l].slice(0, 50));
  }

  async function placeBid(e) {
    e.preventDefault();
    const amt = Number(amount);
    if (!amt || amt <= 0) return alert("Enter positive amount");
    const payload = { auctionId, userId, amount: amt };

    try {
      await axios.post(`${PRODUCER_API}/bid`, payload);
      pushLog(`Placed bid: ${amt}`);
      setAmount("");
    } catch (err) {
      console.error(err);
      pushLog("Failed to send bid");
      alert("Failed to send bid");
    }
  }

  const containerStyle = {
    fontFamily: "Arial, sans-serif",
    padding: 20,
    backgroundColor: "#121212",
    color: "#e0e0e0",
    minHeight: "100vh",
  };

  const panelStyle = {
    flex: 1,
    backgroundColor: "#1e1e2f",
    padding: 20,
    borderRadius: 12,
    boxShadow: "0 4px 12px rgba(0,0,0,0.5)",
  };

  const inputStyle = {
    width: "100%",
    padding: "8px 12px",
    margin: "6px 0 12px",
    borderRadius: 6,
    border: "1px solid #555",
    backgroundColor: "#2c2c3e",
    color: "#e0e0e0",
  };

  const buttonStyle = {
    padding: "10px 20px",
    borderRadius: 6,
    border: "none",
    backgroundColor: "#2979ff",
    color: "#fff",
    cursor: "pointer",
    fontWeight: "bold",
  };

  const logStyle = {
    maxHeight: 400,
    overflowY: "auto",
    backgroundColor: "#1a1a2e",
    padding: 10,
    borderRadius: 8,
  };

  return (
    <div style={containerStyle}>
      <h1 style={{ color: "#2979ff" }}>Live Auction (Kafka Demo)</h1>

      <div style={{ display: "flex", gap: 40 }}>
        <div style={panelStyle}>
          <h2 style={{ color: "#2979ff" }}>Place a Bid</h2>
          <form onSubmit={placeBid}>
            <div>
              <label>Auction ID:</label>
              <input
                style={inputStyle}
                value={auctionId}
                onChange={(e) => setAuctionId(e.target.value)}
              />
            </div>
            <div>
              <label>User ID:</label>
              <input
                style={inputStyle}
                value={userId}
                onChange={(e) => setUserId(e.target.value)}
              />
            </div>
            <div>
              <label>Amount:</label>
              <input
                style={inputStyle}
                type="number"
                value={amount}
                onChange={(e) => setAmount(e.target.value)}
              />
            </div>
            <button style={buttonStyle} type="submit">
              Send Bid
            </button>
          </form>

          <h3 style={{ color: "#2979ff", marginTop: 20 }}>Highest Bids</h3>
          <ul>
            {Object.keys(highestBids).length === 0 && <li>No bids yet</li>}
            {Object.entries(highestBids).map(([aid, h]) => (
              <li key={aid}>
                <b>{aid}</b>: â‚¹{h.amount} by {h.userId} (
                {new Date(h.timestamp).toLocaleTimeString()})
              </li>
            ))}
          </ul>
        </div>

        <div style={{ width: 420, ...panelStyle }}>
          <h2 style={{ color: "#2979ff" }}>Event Log</h2>
          <div style={logStyle}>
            <ul>
              {log.map((l, i) => (
                <li key={i} style={{ fontSize: 13 }}>
                  {l}
                </li>
              ))}
            </ul>
          </div>

          <h2 style={{ color: "#2979ff", marginTop: 20 }}>View Kafka</h2>
          <p>
            Open{" "}
            <a href="http://localhost:8080" target="_blank" rel="noreferrer" style={{ color: "#2979ff" }}>
              Kafka UI
            </a>{" "}
            to see messages in <code>auction-bids</code> topic.
          </p>
        </div>
      </div>
    </div>
  );
}

export default App;
