import React, { useEffect, useState } from "react";
import axios from "axios";

const PRODUCER_API = process.env.REACT_APP_PRODUCER_API || "http://localhost:3001";
const WS_URL = process.env.REACT_APP_WS_URL || "ws://localhost:4000";

function App() {
  const [auctionId, setAuctionId] = useState("auction-1");
  const [userId, setUserId] = useState("user-" + Math.floor(Math.random() * 1000));
  const [amount, setAmount] = useState("");
  const [highestBids, setHighestBids] = useState({});
  const [log, setLog] = useState([]);

  useEffect(() => {
    const ws = new WebSocket(WS_URL);
    ws.onopen = () => pushLog("WS connected");
    ws.onmessage = (ev) => {
      const msg = JSON.parse(ev.data);
      if (msg.type === "snapshot") {
        setHighestBids(msg.highestBids || {});
        pushLog("Received snapshot");
      } else if (msg.type === "highestBid") {
        setHighestBids((prev) => ({ ...prev, [msg.auctionId]: msg.highest }));
        pushLog(`Highest updated for ${msg.auctionId}: ${msg.highest.amount}`);
      } else if (msg.type === "bidReceived") {
        pushLog(`Bid received (not highest) on ${msg.auctionId}: ${msg.bid.amount}`);
      } else {
        pushLog("Unknown message: " + JSON.stringify(msg));
      }
    };
    ws.onclose = () => pushLog("WS disconnected");
    ws.onerror = () => pushLog("WS error");

    return () => ws.close();
  }, []);

  function pushLog(text) {
    setLog((l) => [`${new Date().toLocaleTimeString()} - ${text}`, ...l].slice(0, 50));
  }

  async function placeBid(e) {
    e.preventDefault();
    const amt = Number(amount);
    if (!amt || amt <= 0) return alert("Enter positive amount");
    const payload = { auctionId, userId, amount: amt };

    try {
      await axios.post(`${PRODUCER_API}/bid`, payload);
      pushLog(`Placed bid: ${amt}`);
      setAmount("");
    } catch (err) {
      console.error(err);
      pushLog("Failed to send bid");
      alert("Failed to send bid");
    }
  }

  return (
    <div className="container-fluid bg-dark text-light min-vh-100 p-4">
      {/* Bootstrap CSS embedded */}
      <style>{`
        .text-primary { color: #2979ff !important; }
        .bg-secondary { background-color: #1e1e2f !important; }
        .btn-primary { background-color: #2979ff; border-color: #2979ff; }
        .btn-primary:hover { background-color: #1a5fe5; border-color: #1a5fe5; }
        .form-control { background-color: #2c2c3e; color: #e0e0e0; border: 1px solid #555; border-radius: 0.375rem; }
        .card { border-radius: 0.75rem; }
      `}</style>

      <h1 className="text-primary mb-4">Live Auction (Kafka Demo)</h1>

      <div className="row g-4">
        {/* Bid Panel */}
        <div className="col-lg-6">
          <div className="card bg-secondary text-light shadow">
            <div className="card-body">
              <h2 className="card-title text-primary">Place a Bid</h2>
              <form onSubmit={placeBid}>
                <div className="mb-3">
                  <label className="form-label">Auction ID</label>
                  <input
                    type="text"
                    className="form-control"
                    value={auctionId}
                    onChange={(e) => setAuctionId(e.target.value)}
                  />
                </div>
                <div className="mb-3">
                  <label className="form-label">User ID</label>
                  <input
                    type="text"
                    className="form-control"
                    value={userId}
                    onChange={(e) => setUserId(e.target.value)}
                  />
                </div>
                <div className="mb-3">
                  <label className="form-label">Amount</label>
                  <input
                    type="number"
                    className="form-control"
                    value={amount}
                    onChange={(e) => setAmount(e.target.value)}
                  />
                </div>
                <button type="submit" className="btn btn-primary w-100">
                  Send Bid
                </button>
              </form>

              <h3 className="text-primary mt-4">Highest Bids</h3>
              <ul className="list-group list-group-flush bg-secondary">
                {Object.keys(highestBids).length === 0 && (
                  <li className="list-group-item bg-secondary text-light">No bids yet</li>
                )}
                {Object.entries(highestBids).map(([aid, h]) => (
                  <li key={aid} className="list-group-item bg-secondary text-light">
                    <b>{aid}</b>: ₹{h.amount} by {h.userId} (
                    {new Date(h.timestamp).toLocaleTimeString()})
                  </li>
                ))}
              </ul>
            </div>
          </div>
        </div>

        {/* Log Panel */}
        <div className="col-lg-6">
          <div className="card bg-secondary text-light shadow">
            <div className="card-body">
              <h2 className="card-title text-primary">Event Log</h2>
              <div className="overflow-auto p-2" style={{ maxHeight: "400px" }}>
                <ul className="list-group list-group-flush">
                  {log.map((l, i) => (
                    <li key={i} className="list-group-item bg-secondary text-light small">
                      {l}
                    </li>
                  ))}
                </ul>
              </div>

              <h2 className="card-title text-primary mt-4">View Kafka</h2>
              <p>
                Open{" "}
                <a href="http://localhost:8080" target="_blank" rel="noreferrer" className="text-primary">
                  Kafka UI
                </a>{" "}
                to see messages in <code>auction-bids</code> topic.
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
