import React, { useEffect, useState } from "react";
import axios from "axios";

const PRODUCER_API = process.env.REACT_APP_PRODUCER_API || "http://localhost:3001";
const WS_URL = process.env.REACT_APP_WS_URL || "ws://localhost:4000";

function App() {
  const [auctionId, setAuctionId] = useState("auction-1");
  const [userId, setUserId] = useState("user-" + Math.floor(Math.random() * 1000));
  const [amount, setAmount] = useState("");
  const [highestBids, setHighestBids] = useState({});
  const [log, setLog] = useState([]);

  useEffect(() => {
    const ws = new WebSocket(WS_URL);
    ws.onopen = () => pushLog("WS connected");
    ws.onmessage = (ev) => {
      const msg = JSON.parse(ev.data);
      if (msg.type === 'snapshot') {
        setHighestBids(msg.highestBids || {});
        pushLog('Received snapshot');
      } else if (msg.type === 'highestBid') {
        setHighestBids(prev => ({ ...prev, [msg.auctionId]: msg.highest }));
        pushLog(`Highest updated for ${msg.auctionId}: ${msg.highest.amount}`);
      } else if (msg.type === 'bidReceived') {
        pushLog(`Bid received (not highest) on ${msg.auctionId}: ${msg.bid.amount}`);
      } else {
        pushLog('Unknown message: ' + JSON.stringify(msg));
      }
    };
    ws.onclose = () => pushLog("WS disconnected");
    ws.onerror = () => pushLog("WS error");

    return () => ws.close();
  }, []);

  function pushLog(text) {
    setLog(l => [`${new Date().toLocaleTimeString()} - ${text}`, ...l].slice(0, 50));
  }

  async function placeBid(e) {
    e.preventDefault();
    const amt = Number(amount);
    if (!amt || amt <= 0) return alert('Enter positive amount');
    const payload = { auctionId, userId, amount: amt };

    try {
      await axios.post(`${PRODUCER_API}/bid`, payload);
      pushLog(`Placed bid: ${amt}`);
      setAmount('');
    } catch (err) {
      console.error(err);
      pushLog('Failed to send bid');
      alert('Failed to send bid');
    }
  }

  return (
    <div>
      {/* Bootstrap CDN */}
      <link
        href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.2/dist/css/bootstrap.min.css"
        rel="stylesheet"
        integrity="sha384-9ndCyUa6mY5b2cFQ9ht2qKZk5s3hvq4+0E+ytbJz1hb1z8G+0JRgW9y5b7qd1p6V"
        crossOrigin="anonymous"
      />

      <div className="container py-4" style={{ backgroundColor: '#121212', minHeight: '100vh', color: '#e0e0e0' }}>
        <h1 className="mb-4 text-center text-primary">Live Auction</h1>

        <div className="row">
          {/* Bid Section */}
          <div className="col-md-6 mb-4">
            <div className="card bg-dark text-light shadow-lg">
              <div className="card-body">
                <h4 className="card-title mb-3 text-info">Place a Bid</h4>
                <form onSubmit={placeBid}>
                  <div className="mb-3">
                    <label className="form-label">Auction ID</label>
                    <input
                      type="text"
                      className="form-control bg-secondary text-light border-0"
                      value={auctionId}
                      onChange={e => setAuctionId(e.target.value)}
                    />
                  </div>
                  <div className="mb-3">
                    <label className="form-label">User ID</label>
                    <input
                      type="text"
                      className="form-control bg-secondary text-light border-0"
                      value={userId}
                      onChange={e => setUserId(e.target.value)}
                    />
                  </div>
                  <div className="mb-3">
                    <label className="form-label">Amount (₹)</label>
                    <input
                      type="number"
                      className="form-control bg-secondary text-light border-0"
                      value={amount}
                      onChange={e => setAmount(e.target.value)}
                    />
                  </div>
                  <button type="submit" className="btn btn-primary w-100">Send Bid</button>
                </form>
              </div>
            </div>

            <div className="card bg-dark text-light shadow-lg mt-4">
              <div className="card-body">
                <h5 className="card-title mb-3 text-info">Highest Bids</h5>
                <ul className="list-group list-group-flush">
                  {Object.keys(highestBids).length === 0 && <li className="list-group-item bg-dark text-light">No bids yet</li>}
                  {Object.entries(highestBids).map(([aid, h]) => (
                    <li key={aid} className="list-group-item bg-dark text-light border-0">
                      <b className="text-primary">{aid}</b>: ₹{h.amount} by <span className="text-info">{h.userId}</span> ({new Date(h.timestamp).toLocaleTimeString()})
                    </li>
                  ))}
                </ul>
              </div>
            </div>
          </div>

          {/* Event Log Section */}
          <div className="col-md-6 mb-4">
            <div className="card bg-dark text-light shadow-lg mb-4">
              <div className="card-body">
                <h4 className="card-title mb-3 text-info">Event Log</h4>
                <div style={{ maxHeight: 400, overflowY: 'auto' }}>
                  <ul className="list-group list-group-flush">
                    {log.map((l, i) => <li key={i} className="list-group-item bg-dark text-light small border-0">{l}</li>)}
                  </ul>
                </div>
              </div>
            </div>

            <div className="card bg-dark text-light shadow-lg">
              <div className="card-body">
                <h4 className="card-title mb-3 text-info">View Kafka</h4>
                <p>
                  Open <a href="http://localhost:8080" target="_blank" rel="noreferrer" className="text-primary">Kafka UI</a> to see messages in <code className="text-info">auction-bids</code> topic.
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
